mapper.select:
SELECT id, content_id as contentId, title, tel, addr1, addr2, first_image as firstImage, first_image2 as firstImage2, mapx, mapy, content_type_id as contentTypeId, cat1, cat2, cat3, order_no as orderNo, mlevel, areacode, booktour, cpyrht_div_cd as cpyrhtDivCd, createdtime, modifiedtime, sigungucode, created_date as createdDate, modified_date as modifiedDate FROM tour_api_place WHERE 1=1
AND (title LIKE CONCAT('%', #{keyword}, '%') OR addr1 LIKE CONCAT('%', #{keyword}, '%'))
AND (cat1 = #{contentTypeId} OR cat2 = #{contentTypeId} OR cat3 = #{contentTypeId})
AND areacode = #{areaCode}
ORDER BY id DESC LIMIT #{start}, #{size}

SELECT COUNT(*) FROM tour_api_place
WHERE 1=1

    AND (title LIKE CONCAT('%', #{keyword}, '%')
    OR addr1 LIKE CONCAT('%', #{keyword}, '%'))


    AND (cat1 = #{contentTypeId}
    OR cat2 = #{contentTypeId}
    OR cat3 = #{contentTypeId})


    AND areacode = #{areaCode}

SELECT EXISTS (
    SELECT 1 FROM tour_api_place WHERE content_id = #{contentId}
)
